package Model;import javafx.scene.control.Button;import javafx.scene.image.Image;import javafx.scene.image.ImageView;public class Appointment {	private String srNo;	private String id;	private String patientName;	private String doctorName;	private String departmentName;	private String time;	private String date;	private Boolean visited;	private String prescription;	private Button viewButton;	private Button editButton;	private Button deleteButton;		public Appointment ( String srNo, String id, String patientId, String doctorId, String departmentId, String time, String date, Boolean visited, String prescription, Button viewButton, Button editButton, Button deleteButton ) {		this.srNo = srNo;		this.id = id;		this.patientName = patientId;		this.doctorName = doctorId;		this.departmentName = departmentId;		this.time = time;		this.date = date;		this.visited = visited;		this.prescription = prescription;		Image viewImage = new Image("C:\\Users\\seith\\IdeaProjects\\demo2\\src\\main\\resources\\sample\\demo2\\Images\\ViewEye.png");		ImageView imageView = new ImageView(viewImage);		imageView.setFitWidth(16);		imageView.setFitHeight(16);		viewButton.setGraphic ( imageView );		this.viewButton = viewButton;		viewImage = new Image("C:\\Users\\seith\\IdeaProjects\\demo2\\src\\main\\resources\\sample\\demo2\\Images\\EditPen.png");		imageView = new ImageView(viewImage);		imageView.setFitWidth(16);		imageView.setFitHeight(16);		editButton.setGraphic ( imageView );		this.editButton = editButton;		viewImage = new Image ( "C:\\Users\\seith\\IdeaProjects\\demo2\\src\\main\\resources\\sample\\demo2\\Images\\DeleteTrash.png" );		imageView = new ImageView (viewImage);		imageView.setFitWidth(16);		imageView.setFitHeight(16);		deleteButton.setGraphic ( imageView );		this.deleteButton = deleteButton;	}		public String getSrNo () {		return srNo;	}		public void setSrNo ( String srNo ) {		this.srNo = srNo;	}		public String getId () {		return id;	}		public void setId ( String id ) {		this.id = id;	}		public String getPatientName () {		return patientName;	}		public void setPatientName ( String patientName ) {		this.patientName = patientName;	}		public String getDoctorName () {		return doctorName;	}		public void setDoctorName ( String doctorName ) {		this.doctorName = doctorName;	}		public String getDepartmentName () {		return departmentName;	}		public void setDepartmentName ( String departmentName ) {		this.departmentName = departmentName;	}		public String getTime () {		return time;	}		public void setTime ( String time ) {		this.time = time;	}		public String getDate () {		return date;	}		public void setDate ( String date ) {		this.date = date;	}		public Boolean getVisited () {		return visited;	}		public void setVisited ( Boolean visited ) {		this.visited = visited;	}		public String getPrescription () {		return prescription;	}		public void setPrescription ( String prescription ) {		this.prescription = prescription;	}		public Button getViewButton () {		return viewButton;	}		public void setViewButton ( Button viewButton ) {		this.viewButton = viewButton;	}		public Button getEditButton () {		return editButton;	}		public void setEditButton ( Button editButton ) {		this.editButton = editButton;	}		public Button getDeleteButton () {		return deleteButton;	}		public void setDeleteButton ( Button deleteButton ) {		this.deleteButton = deleteButton;	}}