package Model;public class DoctorAppointment {	private int srNo;	private String name;	private String date;	private String time;	private String prescription;		public String getPrescription () {		return prescription;	}		public void setPrescription ( String prescription ) {		this.prescription = prescription;	}		public String getName () {		return name;	}		public void setName ( String name ) {		this.name = name;	}		public String getDate () {		return date;	}		public void setDate ( String date ) {		this.date = date;	}		public String getTime () {		return time;	}		public void setTime ( String time ) {		this.time = time;	}		public int getSrNo () {		return srNo;	}		public void setSrNo ( int srNo ) {		this.srNo = srNo;	}}