package sample.demo2;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Button;import javafx.scene.control.Label;import javafx.scene.control.TextField;import javafx.stage.Stage;import java.io.IOException;import java.util.Objects;public class AdminAddDepartmentController {	@FXML	private Button addDepartmentButton;		@FXML	private TextField departmentName;		@FXML	private Label invalidLabel;		boolean validity = true;	private Stage stage;	private Scene scene;	private Parent root;	DatabaseConnection database = DatabaseConnection.getInstance ();		public void removeFieldBorder () {		departmentName.setStyle("-fx-border-style: none;");			}		public void checkFieldsValidity () {		validity = true;		if ( departmentName.getText().isBlank()) {			departmentName.setStyle("-fx-border-color: #eb1111b5;");			validity = false;		}			}		public void addDepartmentAction () {				removeFieldBorder ();		checkFieldsValidity ();				if ( validity ) {			fillData();		}		else {			invalidLabel.setText("Please Write Carefully And Fill out All the Fields!");		}	}		public void fillData() {		try {			DatabaseConnection database = new DatabaseConnection ();															boolean registered = database.departmentExist ( departmentName.getText () );						if ( registered ) {				invalidLabel.setText ( "Department Already Registered!" );				departmentName.setStyle ( "-fx-border-color: #eb1111b5;" );							} else {				database.addDepartment ( departmentName.getText ());				toAllDepartment ( );			}					} catch ( Exception e ) {			System.out.println (e.getMessage ());		}	}		public void toAllDepartment () {		try {			FXMLLoader loader = new FXMLLoader ( getClass ().getResource ( "adminAllDepartment.fxml" ) );			root = loader.load ();			scene = new Scene ( root );			stage = (Stage) addDepartmentButton.getScene ().getWindow ();			stage.setScene ( scene );		} catch (Exception e) {			System.out.println (e.getMessage ());		}	}		public void toAllPatient ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllPatients.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAllAdmin ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllAdmin.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAdminHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}	public void toAllDoctor ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllDoctor.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}