package sample.demo2;import Model.Department;import Model.Doctor;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.fxml.Initializable;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.TextField;import javafx.stage.Stage;import java.io.IOException;import java.net.URL;import java.util.Objects;import java.util.ResourceBundle;public class AdminEditSingleDoctorController implements Initializable {	@FXML	private TextField addressField, departmentField, cityField, cnicField, emailField, idField, fnameField, lnameField, phoneField;		private static String doctorId;	private Stage stage;	private Scene scene;	private Parent root;	DatabaseConnection database = DatabaseConnection.getInstance ();	public static void setDoctorId ( String doctorId ) {		AdminEditSingleDoctorController.doctorId = doctorId;	}	Doctor doctor = database.getDoctorById ( doctorId );			Department departmentName = database.getDepartmentById ( doctor.getDepartment () );	@Override	public void initialize ( URL url, ResourceBundle resourceBundle ) {		try {						String[] name = doctor.getName ().split ( " " );						idField.setText ( doctor.getId () );			fnameField.setText ( name[0] );			lnameField.setText (name[1]);			phoneField.setText ( doctor.getPhone () );			emailField.setText ( doctor.getEmail () );			cnicField.setText ( doctor.getCnic () );			cityField.setText ( doctor.getCity () );			departmentField.setText ( departmentName.getName () );			addressField.setText ( doctor.getHouse () );					} catch (Exception e) {			e.printStackTrace ();		}			}		public void updateDoctor( ActionEvent event) {		try {			database.updateDoctorById ( idField.getText (), fnameField.getText (), lnameField.getText (), cnicField.getText (), phoneField.getText (), emailField.getText (), addressField.getText (), cityField.getText (), doctor.getDepartment () );			toAllDoctor ( event );		} catch (Exception e) {			e.printStackTrace ();		}	}		public void toAllPatient ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllPatients.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAllAdmin ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllAdmin.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAdminHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}	public void toAllDoctor ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllDoctor.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}