package sample.demo2;import Model.Department;import Model.Doctor;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.fxml.Initializable;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Label;import javafx.stage.Stage;import java.io.IOException;import java.net.URL;import java.util.Objects;import java.util.ResourceBundle;public class AdminViewSingleDoctorController implements Initializable {	@FXML	private Label address, age, city, cnic, department, email, id, name, phone;	private static String doctorId;	private Stage stage;	private Scene scene;	private Parent root;	public static void setDoctorId ( String doctorId ) {		AdminViewSingleDoctorController.doctorId = doctorId;	}		@Override	public void initialize ( URL url, ResourceBundle resourceBundle ) {		try {			DatabaseConnection database = DatabaseConnection.getInstance ();			Doctor doctor = database.getDoctorById ( doctorId );						Department departmentName = database.getDepartmentById ( doctor.getDepartment () );						id.setText ( doctor.getId () );			name.setText ( doctor.getName () );			address.setText ( doctor.getHouse () );			age.setText ( doctor.getAge () );			cnic.setText ( doctor.getCnic () );			city.setText ( doctor.getCity () );			department.setText ( departmentName.getName () );			email.setText ( doctor.getEmail () );			phone.setText ( doctor.getPhone () );								} catch (Exception e) {			e.printStackTrace ();		}	}		public void toAllPatient ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllPatients.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAllAdmin ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllAdmin.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAdminHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}	public void toAllDoctor ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllDoctor.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}