package sample.demo2;import Model.Patient;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.fxml.Initializable;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Label;import javafx.stage.Stage;import java.io.IOException;import java.net.URL;import java.util.Objects;import java.util.ResourceBundle;public class AdminViewSinglePatientController implements Initializable {	@FXML	private Label patientAddress, patientAge, patientCity, patientCnic, patientEmail, id, patientName, patientPhone;	private static String patientId;	private Stage stage;	private Scene scene;	private Parent root;	public static void setPatientId ( String adminId ) {		AdminViewSinglePatientController.patientId = adminId;	}		@Override	public void initialize ( URL url, ResourceBundle resourceBundle ) {		try {			DatabaseConnection database = DatabaseConnection.getInstance ();			Patient patient = database.getPatientById ( patientId );			id.setText ( patient.getId () );			patientName.setText ( patient.getName () );			patientAddress.setText ( patient.getHouse () );			patientAge.setText ( patient.getAge () );			patientCity.setText ( patient.getCity () );			patientCnic.setText ( patient.getCnic () );			patientEmail.setText ( patient.getEmail () );			patientPhone.setText ( patient.getPhone () );		} catch (Exception e) {			e.printStackTrace ();		}	}		public void toAllPatient ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllPatients.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAllAdmin ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllAdmin.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void toAdminHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}	public void toAllDoctor ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("adminAllDoctor.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}	public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}