package sample.demo2;import javafx.scene.control.TextField;import java.util.regex.Matcher;import java.util.regex.Pattern;public class AlphabetValidator {		private static final Pattern APLHABET_PATTERN = Pattern.compile("^[a-zA-Z]+$");		public static boolean isValid( TextField textField) {		String string = textField.getText();		Matcher matcher = APLHABET_PATTERN.matcher(string);		return matcher.matches();	}}