package sample.demo2;import javafx.scene.control.TextField;import java.util.regex.Matcher;import java.util.regex.Pattern;public class CNICValidator {	private static final Pattern NUMBER_PATTERN = Pattern.compile("^\\d{13}+$");		public static boolean isValid( TextField textField) {		String number = textField.getText();		Matcher matcher = NUMBER_PATTERN.matcher(number);		return matcher.matches();	}}