package sample.demo2;import Model.DoctorAppointment;import javafx.fxml.FXML;import javafx.scene.control.Label;public class DoctorAppointmentItemController {		@FXML	private Label date;		@FXML	private Label patName;		@FXML	private Label prescription;		@FXML	private Label srNo;		@FXML	private Label time;		public void loadAppointment( DoctorAppointment appointment ) {		srNo.setText ( "" + appointment.getSrNo () );		patName.setText(appointment.getName ());		date.setText ( appointment.getDate () );		time.setText ( appointment.getTime () );		prescription.setText ( appointment.getPrescription () );	}}