package sample.demo2;import Model.Appointment;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.fxml.Initializable;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Label;import javafx.scene.control.TextArea;import javafx.scene.control.TextField;import javafx.stage.Stage;import java.io.IOException;import java.net.URL;import java.util.Objects;import java.util.ResourceBundle;public class DoctorEditSingleAppointmentController implements Initializable {	private static String appointmentId;		public static void setAppointmentId ( String appointmentId ) {		DoctorEditSingleAppointmentController.appointmentId = appointmentId;	}		@FXML	private TextField dateField, departmentField, id, patientField, timeField, visitedField;		@FXML	private TextArea prescriptionField;		private Stage stage;	private Scene scene;	private Parent root;		DatabaseConnection database = DatabaseConnection.getInstance ();	@Override	public void initialize ( URL url, ResourceBundle resourceBundle ) {		try {			Appointment appointment = database.getAppointmentById ( appointmentId );			id.setText ( appointment.getId () );			patientField.setText ( appointment.getPatientName () );			departmentField.setText ( appointment.getDepartmentName () );			dateField.setText ( appointment.getDate () );			timeField.setText ( appointment.getTime () );			visitedField.setText ( appointment.getVisited ()? "Yes" : "No" );			prescriptionField.setText ( appointment.getPrescription () );		} catch (Exception e) {			e.printStackTrace ();			System.out.println (e.getMessage ());		}	}		public void updateAction (ActionEvent event) throws IOException {		database.updateAppointmentById ( appointmentId, prescriptionField.getText () );		toDoctorHome ( event );	}				public void toDoctorHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("doctorHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}