package sample.demo2;import Model.Appointment;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.fxml.Initializable;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Label;import javafx.scene.control.TextArea;import javafx.stage.Stage;import java.io.IOException;import java.net.URL;import java.util.Objects;import java.util.ResourceBundle;public class DoctorViewSingleAppointmentController implements Initializable {	@FXML	private Label dateField, departmentField, id, patientField, timeField, vistedField;		@FXML	private TextArea prescriptionField;		private Stage stage;	private Scene scene;	private Parent root;		private static String appointmentId;		public static void setAppointmentId ( String appointmentId ) {		DoctorViewSingleAppointmentController.appointmentId = appointmentId;	}		@Override	public void initialize ( URL url, ResourceBundle resourceBundle ) {		try {			DatabaseConnection database = DatabaseConnection.getInstance ();			Appointment appointment = database.getAppointmentById ( appointmentId );			id.setText ( appointment.getId () );			patientField.setText ( appointment.getPatientName () );			departmentField.setText ( appointment.getDepartmentName () );			dateField.setText ( appointment.getDate () );			timeField.setText ( appointment.getTime () );			vistedField.setText ( appointment.getVisited ()? "Yes" : "No" );			prescriptionField.setText ( appointment.getPrescription () );		} catch (Exception e) {			e.printStackTrace ();			System.out.println (e.getMessage ());		}	}				public void toDoctorHome ( ActionEvent event) throws IOException {		root = FXMLLoader.load( Objects.requireNonNull(getClass().getResource("doctorHome.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene (root);		stage.setScene(scene);		stage.show();	}		public void logout ( ActionEvent event) throws IOException {		root = FXMLLoader.load(Objects.requireNonNull(getClass().getResource("landing.fxml")));		stage = (Stage) ((Node)event.getSource()).getScene().getWindow();		scene = new Scene(root);		stage.setScene(scene);		stage.show();	}}