package sample.demo2;import javafx.scene.control.TextField;import java.util.regex.Matcher;import java.util.regex.Pattern;public class NumberValidator {	private static final Pattern NUMBER_PATTERN = Pattern.compile("^03\\d{9}+$");		public static boolean isValid( TextField textField) {		String number = textField.getText();		Matcher matcher = NUMBER_PATTERN.matcher(number);		return matcher.matches();	}}