package sample.demo2;import Model.DoctorAppointment;import Model.PatientAppointment;import javafx.fxml.FXML;import javafx.scene.control.Label;public class PatientAppointmentItemController {		@FXML	private Label date, patName, prescription, srNo, time;			public void loadAppointment( PatientAppointment appointment ) {		srNo.setText ( "" + appointment.getSrNo () );		patName.setText(appointment.getName ());		date.setText ( appointment.getDate () );		time.setText ( appointment.getTime () );		prescription.setText ( appointment.getPrescription () );	}}